import React from 'react';
import "./styles.css";
import Navbar from '../src/components/Navbar';
import Searchbar from './components/Searchbar';
import Pokedex from './components/Pokedex';
import { getPokemonData, getPokemons } from './components/api';
import Pokemon from './components/Pokemon';
import { FavoriteProvider } from './components/contexts/favoritesContext';

const {useState, useEffect} = React;

const localStorageKey = "favorite_pokemon";

export default function App() {
  const [pokemons, setPokemons] = useState([]);
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const [favorites, setFavorites] = useState(["raichu"]);

  const fetchPokemons = async () => {
try {
setLoading(true);
const data = await getPokemons(25, 25 * page); 
const promises = data.results.map(async(pokemon) => {
return await getPokemonData(pokemon.url)
})
const results = await Promise.all(promises)
setPokemons(results)
setLoading(false);
setTotal(Math.ceil(data.count / 25))
} catch(err){}

};

const loadFavoritePokemons = () => {
  const pokemons = JSON.parse (window.localStorage.getItem(localStorageKey)) || [];
  setFavorites(pokemons);

};

useEffect(() => {
  loadFavoritePokemons();
  }, []);



useEffect(() => {
  console.log("Obteniendo TODOS los pokemones favoritos");
fetchPokemons();
}, [page]);


 const updateFavoritePokemons = (name) => {
   const updated = [...favorites];
   const isFavorite = updated.indexOf(name);
   if(isFavorite >= 0) {
     updated.splice(isFavorite, 1);
   } else {
     updated.push(name);
   }
   setFavorites(updated);
   window.localStorage.setItem(localStorageKey, JSON.stringify(updated));
 
};


  return (
    <FavoriteProvider value={{
      favoritePokemons: favorites,
      updateFavoritePokemons: updateFavoritePokemons
      }} >

    <div>
      <Navbar />
    <div className="App">
       <Searchbar />
       <Pokedex 
       loading={loading}
       pokemons={pokemons} 
       page={page} 
       setPage={setPage}
       total={total}
       />
    </div> 
    </div>
    </FavoriteProvider>
  );
}
